const Header = () => {
  const [isOpen, setIsOpen] = useState(false)

  const handleIsOpenMenu = () => {
    setIsOpen(true)
  }

  return (
    <div className='headerContainer'>
      <img className='logo' src={logo} />
      <ul className='header'>
        <li><Link to='/' id="home" className="menu-item">Accueil</Link></li>
        <li><Link to='/Tarifs' id="about" className="menu-item">Tarifs</Link></li>
        <li><Link to='Contact' id="contact" className="menu-item">Contact</Link></li>
        <a onClick={handleIsOpenMenu} className="menu-item--small" href="">Settings</a>
      </ul>
    </div>
  )
}

export default Header
