import React, { useState } from 'react'

import './Admin.css'

export default function Admin() {
  const [title, setTitle] = useState('')
  const [description, setDescription] = useState('')
  const [imageLink, setImageLink] = useState('')

  const onChangeTitle = (event) => {
    const { value } = event.target
    setTitle(value)
  }

  const onChangeDescription = (event) => {
    const { value } = event.target
    setDescription(value)
  }

  const onChangeImageLink = (event) => {
    const { value } = event.target
    setImageLink(value)
  }

  return (
    <div className='Admin'>
      <div className="titreForm">
        <label>Titre de l'article :</label>
        <input placeholder='titre' value={title} />
      </div>
      <div className="descriptionForm">
        <div className="textDescriptionForm">Description de l'article :</div>
        <textarea
          placeholder="Décrivez votre article en quelques mots"
          id="story"
          name="story"
          rows="5"
          cols="138"
          value={description}
        />
      </div>
      <div className="photoLinkForm">
        <label>Lien de l'image</label>
        <input placeholder="lien de l'image" value={imageLink} />
      </div>
    </div>
  )
}
