import React, { useState } from 'react'
import axios from 'axios'

import './Admin.css'

export default function Admin() {
  const [title, setTitle] = useState('')
  const [description, setDescription] = useState('')
  const [imageLink, setImageLink] = useState('')

  const onChangeTitle = (event) => {
    const { value } = event.target
    setTitle(value)
  }

  const onChangeDescription = (event) => {
    const { value } = event.target
    setDescription(value)
  }

  const onChangeImageLink = (event) => {
    const { value } = event.target
    setImageLink(value)
  }

  const handleSubmit = () => {
    axios.post('http://localhost:4000/articles/', {
      art_name: 'Zidane',
      art_description: 'Flintstone',
      art_photo: 'https://cdn.pixabay.com/photo/2019/12/25/09/33/fruit-plate-4718174_1280.jpg',
      art_cat_id: 1,
    })
      .then(function (response) {
        console.log(response);
      })
      .catch(function (error) {
        console.log(error);
      });
  }

  return (
    <div className='Admin'>
      <form onSubmit={handleSubmit}>
        <div className="titreForm">
          <label>Titre de l'article :</label>
          <input placeholder='titre' value={title} onChange={onChangeTitle} />
        </div>
        <div className="descriptionForm">
          <div className="textDescriptionForm">Description de l'article :</div>
          <textarea
            placeholder="Décrivez votre article en quelques mots"
            id="story"
            name="story"
            rows="5"
            cols="138"
            value={description}
            onChange={onChangeDescription}
          />
        </div>
        <div className="photoLinkForm">
          <label>Lien de l'image</label>
          <input placeholder="lien de l'image" value={imageLink} onChange={onChangeImageLink} />
        </div>
        <button type="submit">Valider</button>
      </form>
    </div>
  )
}
