import React, { useEffect, useState } from 'react'
import { Link } from 'react-router-dom'
import { debounce } from "debounce";
import HamburgerMenu from 'react-hamburger-menu'

import logo from './images/mecavelo.jpg'
import './Header.css'

const Header = () => {
  const [isOpen, setIsOpen] = useState(false)

  const handleClick = () => {
    setIsOpen(!isOpen)
  }
  // Debounce resize
  const [dimensions, setDimensions] = useState({
    width: window.innerWidth
  })

  useEffect(() => {
    const debouncedHandleResize = debounce(function handleResize() {
      setDimensions({
        width: window.innerWidth
      });
    }, 100);

    window.addEventListener("resize", debouncedHandleResize);

    return () => window.removeEventListener("resize", debouncedHandleResize)
  });

  return (
    <div className='headerContainer'>
      {dimensions.width > 550 ?
        <>
          <img className='logo' src={logo} alt='logo' />
          <ul className='header'>
            <li><Link to='/' className='accueil'>Accueil</Link></li>
            <li><Link to='/Tarifs' className='tarif'>Tarifs</Link></li>
            <li><Link to='Contact' className='contact'>Contact</Link></li>
          </ul>
        </>
        :
        <>
          <HamburgerMenu
            isOpen={isOpen}
            menuClicked={handleClick}
            width={18}
            height={15}
            strokeWidth={1}
            rotate={0}
            color='black'
            borderRadius={0}
            animationDuration={0.5}
          />
        </>}
    </div>
  )
}

export default Header
